lambda_client = boto3.client(service_name='lambda', region_name='us-east-1')


def run_scraper(query_parameters):
    print("running")
    sources = query_parameters["sources"]
    keywords_list = query_parameters["keywords_list"]
    subreddits_list = []
    if "reddit" in sources:
        subreddits_list = query_parameters["subreddits_list"]
    if "twitter" in sources:
        # update function configuration to new search text.
        # love OR hate finds tweets containing either ‚Äúlove‚Äù or ‚Äúhate‚Äù (or both).
        search_text = ' OR '.join(keywords_list)
        print("Twitter search text", search_text)
        lambda_client.update_function_configuration(
            FunctionName="aws-serverless-repository-aws-TwitterSearchPoller-6GH8Y61OUDGH",
            Environment={
                'Variables': {
                    'SEARCH_TEXT': search_text
                }
            },
        )
        # Now the twitter poller will trigger the process_tweets function with tweets matching search_text

    # Scrape the data from sources
    # twitter is polled using a lambda function which triggers my processTweets lambda function when it finds new tweets
    print("scraping")
    reddit = scraper.init_reddit_scraper()
    submissions, analysis_results = scraper.scrape_submissions_from_subreddits(reddit, subreddits_list, keywords_list)
    analysis_results["sources"] = sources
    analysis_results["keywords"] = keywords_list

    # Analyze the text
    # (desired) side effect: analysis_results will be modified
    print("analyzing sentiment")
    sentimenter.analyze_reddit(submissions, analysis_results)

    print("submissions", submissions)
    print("analysis_results", analysis_results)
    return analysis_results


